import React, { Component } from 'react';
import {connect} from "react-redux";
import {auth} from "../../actions";
import '../static/Home.css';
import {Link} from 'react-router-dom';
import { FaEnvelope,FaCode,FaUser,FaNewspaper,FaIdCard,FaUsers,FaSignOutAlt} from 'react-icons/fa';
import IconButton from '@material-ui/core/IconButton';
import {Redirect} from 'react-router-dom';

class Navbar extends Component{
//
//   handleLogout = e => {
//     console.log("logout");
//     this.props.logout();
//     return <Redirect to="/" />;
// }
// <Link to="/about"><IconButton>
//   <FaIdCard />
// </IconButton></Link>

  redirectAbout = e =>{
    return <Redirect to="/about" />;
  }

  getInitialState() {
  return {
    isMouseInside: false
  };
}
mouseEnter = () => {
  this.setState({ isMouseInside: true });
}
mouseLeave = () => {
  this.setState({ isMouseInside: false });
}

  componentDidMount(){
    if (this.props.isAuthenticated==="False") {
      return <Redirect to="/" />;
    }
  }

  render(){

    return(

      <div className="header header-fill header-fixed">
        <div className="header-brand">
          <div className="nav-item no-hover">
              <Link to="/dashboard"><h1 className="title">Guru</h1></Link>
          </div>
        </div>

        <div className="nav-right" >
          <div className=" nav-item text-center"><p>{this.props.user.username}</p></div>
          <div className="nav-item text-center">
              <IconButton>
                <FaEnvelope/>
              </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton>
              <FaCode/>
            </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton>
              <FaUser />
            </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton>
              <FaNewspaper/>
            </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton>
              <FaUsers/>
            </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton onClick={this.redirectAbout}>
              <FaIdCard />
            </IconButton>
          </div>
          <div className="nav-item text-center">
            <IconButton onClick={this.props.logout}>
              <FaSignOutAlt />
            </IconButton>
          </div>

        </div>
      </div>

    );
  }
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
        isAuthenticated: state.auth.isAuthenticated
    }
}

const mapDispatchToProps = dispatch => {
    return {
        logout: () => dispatch(auth.logout()),
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Navbar);
