import React, { Component } from 'react';
import Navbar from './Navbar';
import '../static/Connect.css';
import {Link} from 'react-router-dom';
import {connect} from "react-redux";
import {FaSearch} from 'react-icons/fa';
import {Tabs,TabLink,TabContent} from 'react-tabs-redux'
import {headers} from '../forms/global.js'


class Connect extends Component{

  state ={
    users:[],
    avis:null,
    query:null,
    user_query:[]
  }

  componentWillMount(){
    fetch(`/api/users/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
     .then (json =>this.setState({users: json})).catch(err => {
           console.log("fetch error" + err);
       });

  fetch(`/api/avis/`)
    .then(response => { return response.json();}).then(responseData => {return responseData;})
   .then (json =>this.setState({avis: json})).catch(err => {
         console.log("fetch error" + err);
     });
  }

  search=(e)=>{
    e.preventDefault();
    let query = this.state.query
    let body = JSON.stringify({query});
    headers["Authorization"] = `Token ${this.props.token}`;
    fetch("/api/filter/", {headers,body,method:"POST"}).then(res => {return res.json();})
    .then(json =>{this.setState({users: json},console.log(this.state.users))})
    .catch(err => {console.log("fetch error" + err)})
  }

  render(){
    if(this.state.avis===null){
      return<div>Loading</div>
    }
    return(
    <div id="connect">
      <header>
        <Navbar/>
      </header>
    <div className="flex-box">
      <div className="connect-body">
          <div id="search-bar"><input type="text" onChange={e =>this.setState({query:e.target.value})}/><FaSearch onClick={this.search.bind(this)}id="search-icon"/></div>
          <Tabs>
            <TabLink to="tab1">All</TabLink>
            <TabLink to="tab2">Tab2</TabLink>
            <TabLink to="tab3">Tab3</TabLink>

            <TabContent for="tab1">
              <div id="users">
            {
              this.state.users.map((el,idx) => {
                if(el.id!==this.props.user.id && el.id !== 1){
                  return <div className="user-icon" key={el.id}>
                  <div>
                       <Link to={"/about/"+el.username}><img alt="profile-pic"  className="pro-pic"src={this.state.avis[idx].avi_path}/>
                         <h3 className="username main res-item">@{el.username}</h3>
                       <p className="desc res-item">{el.type}</p></Link>
                       </div>
                         </div>}
                  return null
              })}
              </div>
            </TabContent>

            <TabContent for="tab2">
                <div id="users">
                </div>
            </TabContent>
            <TabContent for="tab3">
              <div id="users">
              </div>
            </TabContent>
          </Tabs>

      </div>
      </div>

    </div>
    );
  }
}


const mapStateToProps = state => {
    return {
        user: state.auth.user,
    }
}


export default connect(mapStateToProps)(Connect);
