import React, { Component } from 'react';
import Navbar from './Navbar';


class Connect extends Component{

  state ={
    users:[],
    aboutUsers:null,
    avis:null,
  }

  componentWillMount(){
    fetch(`/api/users/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
     .then (json =>this.setState({users: json})).catch(err => {
           console.log("fetch error" + err);
       });

   fetch(`/api/aboutUsers/`)
     .then(response => { return response.json();}).then(responseData => {return responseData;})
    .then (json =>this.setState({aboutUsers: json})).catch(err => {
          console.log("fetch error" + err);
      });

  fetch(`/api/avis/`)
    .then(response => { return response.json();}).then(responseData => {return responseData;})
   .then (json =>this.setState({avis: json})).catch(err => {
         console.log("fetch error" + err);
     });
  }

  render(){
     const { users,aboutUser, avi } = this.state;
    if(this.state.avis.length===0){
      return<div>Loading</div>
    }
    return(
    <div>
      <header>
        <Navbar/>
      </header>

      <div id="body">
        {
          users.map((el,idx) => {
              return <div key={el.id}>
                   <h3 className="main res-item">{el.username}</h3>
              </div>
          })}

          {
            users.map(function(item,index) {
              return item.id, (this.state.avis[index].avi_path)
            }  , this)
          }









      </div>


    </div>
    );
  }
}

export default (Connect);
