import React, { Component } from 'react';
import '../static/Settings.css';
import {connect} from 'react-redux';
import {auth} from "../../actions";
import Navbar from './Navbar';
import Popup from '../forms/Popup';

class UserSettings extends Component {

  state = {
    user:{},
    showPopup: false,
   };


  togglePopup= e => {
    e.preventDefault();
    this.setState({
     showPopup: !this.state.showPopup
    });
  }

  onSubmit = e => {
    e.preventDefault();
    this.props.login(this.state.username, this.state.password);
  }

  handleChange(param,value){
    let updatedUser = this.state.user;
    updatedUser[param]=value;
    this.setState({user:updatedUser})
  }



  render () {
  return (
    <div id="user-settings">
      <header>
        <Navbar history={this.props.history}/>
      </header>
      <div className="flex-box">
        <div className="settings-body">

        <div className="">
          <form>
              <input defaultValue={this.props.user.first_name} className="" onChange={e => this.handleChange("first_name",e.target.value)}
              maxLength="200" placeholder="First Name" type="text"/>
              <input defaultValue={this.props.user.last_name} className="" onChange={e => this.handleChange("last_name",e.target.value)}
              maxLength="100" placeholder="Last Name" type="text"/>
              <input defaultValue={this.props.user.username} className="" onChange={e => this.handleChange("username",e.target.value)}
              maxLength="100" placeholder="Username" type="text"/>
              <input defaultValue={this.props.user.email} className="" onChange={e => this.handleChange("email",e.target.value)}
              maxLength="100" placeholder="Email" type="email"/>
             <button className="" onClick={this.togglePopup.bind(this)}>Delete Account</button>
             <button className ="submit">Save</button>
          </form>
        </div>

        {this.state.showPopup ?
        <Popup
                  text='Click "Close Button" to hide popup'
                  closePopup={this.togglePopup.bind(this)}
        />
        : null
        }

        </div>
      </div>
    </div>

      );
   }
}


const mapDispatchToProps = dispatch => {
  return {
    deleteUser: (user) => {
      return dispatch(auth.deleteUser(user));
    }
  };
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
        token: state.auth.token,
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserSettings);
