import React, { Component } from 'react';
import {connect} from "react-redux";
import {auth} from "../../actions";
import '../static/Home.css';


class Settings extends Component {

  state = {
    username: "",
    email:"",
    first_name:"",
    last_name:"",
    password: "",
    type:"ME",
  }

  registerSubmit = e => {
    e.preventDefault();
    if(this.state.password === document.getElementById("confirm_pass").value){
          this.props.register(this.state.username,this.state.email,this.state.first_name,
          this.state.last_name,this.state.password,this.state.type,this.state.username);
    }else{
       alert("Password does not match");
     }
  }

  render () {
      return (
    <div className="form-container">
    <p id = "banner"> Account Settings </p>
      <form>
        <div className="form-group">
          <input defaultValue={this.props.user.first_name} className="" onChange={e => this.handleChange("first_name",e.target.value)}
          maxLength="200" placeholder="First Name" type="text"/>
          <input defaultValue={this.props.user.last_name} className="" onChange={e => this.handleChange("last_name",e.target.value)}
          maxLength="100" placeholder="Last Name" type="text"/>
        </div>
          <input defaultValue={this.props.user.username} className="" onChange={e => this.handleChange("username",e.target.value)}
          maxLength="100" placeholder="Username" type="text"/>
          <input defaultValue={this.props.user.email} className="" onChange={e => this.handleChange("email",e.target.value)}
          maxLength="100" placeholder="Email" type="email"/>

        <select defaultValue={this.props.user.type} className="" onChange={e => this.handleChange("type",e.target.value)}>
          <option value="ME">Mentee</option>
          <option value="MR">Mentor</option>
        </select>

         <button className="" onClick={this.togglePopup.bind(this)}>Delete Account</button>
         <button className ="submit">Save</button>
      </form>
      </div>

      );
   }
}

const mapStateToProps = state => {
  let errors = [];
  if (state.auth.errors) {
    errors = Object.keys(state.auth.errors).map(field => {
      return {field, message: state.auth.errors[field]};
    });
  }
  return {
    errors,
    isAuthenticated: state.auth.isAuthenticated
  };
}

const mapDispatchToProps = dispatch => {
  return {
    register: (username,email,first_name,last_name,password,type) =>
    dispatch(auth.register(username,email,first_name,last_name,password,type)),
  };
}


export default connect(mapStateToProps, mapDispatchToProps)(Register);
