const initialState = {
  education:[],
};


export default function education(state=initialState, action) {
  let educationList = state.education.slice();

  switch (action.type) {
    case 'FETCH_EDUCATION':
      return{...state,education:action.edu};

    case 'ADD_EDUCATION':
      educationList += action.edu_obj
      return{...state, education:educationList};

    case 'DELETE_EDUCATION':
      educationList.splice(action.id,1);
      return {...state, education:educationList};

    default:
      return state;

  }
}
