import React, { Component } from 'react';
import {connect} from "react-redux";
import '../static/Home.css';
import Navbar from './Navbar';
import '../static/Forum.css';
import {FaTimes,FaPlus} from 'react-icons/fa';
import TextareaAutosize from 'react-textarea-autosize';

class Topic extends Component{

    state = {
      posts:[],
      topic:"",
      post:"",
    }

    componentWillMount(){
      fetch(`/api/post/${this.props.match.params.topic_id}/`)
        .then(response => { return response.json();}).then(responseData => {return responseData;})
        .then (json => {this.setState({posts: json.data}); this.setState({topic:json.topic})})
        .catch(err => {console.log("fetch error" + err);
         });
    }


  render(){
    console.log(this.state.post)
    return(
    <div id="topic">
      <header>
        <Navbar/>
      </header>
      <div className="flex-box">
        <div className="forum-body">
            <div id="post-list">
              <h1 className="forum-h1">{this.state.topic}</h1>
              <div className="post">
                <TextareaAutosize id="new-post" onChange={e => this.setState({post: e.target.value})} maxlength="500" minRows={1} maxRows={6} placeholder="Create a post..."/>
                <button className="post-submit">Post</button> <button className="post-submit">Add Tag</button>
              </div>
              <div>
                {this.state.posts.map((el,idx) => {
                      return <div className="post" key={idx}>
                           <FaTimes className={this.state.hidden ? 'hidden':'deleteSkill'} />
                           <p className="desc res-item">{el.text}</p>
                      </div>
                  })}
              </div>
            </div>

        </div>
      </div>
    </div>
    )
  }
}

const mapStateToProps = state => {
    return {
      user: state.auth.user,
    }
}

export default connect(mapStateToProps)(Topic);
