import React, { Component } from 'react';
import {connect} from "react-redux";
import Navbar from './Navbar';
import '../static/About.css';
import Skills from '../forms/Skills'
import Interests from '../forms/Interests'
import Experience from '../forms/Experience'
import Education from '../forms/Education'
import AboutUser from '../forms/AboutUser'
import {FaCode,FaEdit} from 'react-icons/fa';
import IconButton from '@material-ui/core/IconButton';


class About extends Component{
  parse_type(type){
    return (type ==='MR' ? 'Mentor' : 'Mentee')
  }

  show = (e) =>{
    this.setState({hidden:!this.state.hidden});
  }
  render(){
    return(
    <div className="body" id="about">
      <header>
        <Navbar history={this.props.history}/>
      </header>
      <div className="flex-box">
        <div className="about-body">

          <section id="aboutUser">
            <AboutUser/>
          </section>

          <section id="resume">
          <span className="caption"><h1 id="name">{this.props.user.first_name} {this.props.user.last_name}</h1>
          <FaCode className="left"/>{this.parse_type(this.props.user.type)}
          <IconButton onClick={this.show}><FaEdit  className="about-expand"/></IconButton>
          </span>
            <article>
              <Experience/>
            </article>

            <article>
              <Education/>
            </article>

            <article>
              <Skills/>
            </article>

            <article>
              <Interests/>
            </article>
          </section>
        </div>
      </div>
      <div>{this.props.children}</div>
    </div>
    );
  }
}
const mapStateToProps = state => {
    return {
        user: state.auth.user,
        token: state.auth.token,
    }
}



export default connect(mapStateToProps)(About);
