const initialState = {
  skills:null,
  isLoading:true,
};


export default function skills(state=initialState, action) {
  // let skillList = state.slice();

  switch (action.type) {
    case 'FETCH_SKILLS':
      return{ ...state,skills:action.skills};

    case 'ADD_SKILL':
      return state;

      // return[...state, action.skill];

    case 'DELETE_SKILL':
      return state;
      //
      // skillList.splice(action.id,1);
      // return skillList;

    default:
      return state;

  }
}
