import React, { Component } from 'react';
import {connect} from "react-redux";
import {education} from "../../actions";
import {getCookie} from './get_csrf'
import IconButton from '@material-ui/core/IconButton';
import {FaTimes,FaPlus} from 'react-icons/fa';

class Education extends Component{

  state ={
    school:"",
    degree:"",
    location:"",
    start:"",
    end:null,
    education:[],
    csrftoken:getCookie('csrftoken'),
  }


  eduSubmit = e => {
    e.preventDefault();
    this.addEducation(this.props.user.id,this.state.start,this.state.end,this.state.school, this.state.location, this.state.degree);
    this.setState({education:[]})
    }


addEducation(user_id,start,end,school,location,degree){
  const token = localStorage.getItem("token");
  let body = JSON.stringify({user_id,start,end,school,location,degree});
  let headers = {
    'Content-Type': 'application/json',
    'X-CSRFToken': this.state.csrftoken,
  };
  if (token) {
    headers["Authorization"] = `Token ${token}`;
  }
  fetch(`api/education/`,{headers,body,method:"POST",}).then(res => {return res.json();}).catch(err => {
            console.log("fetch error" + err)}).then(this.fetchEducation());
}

async fetchEducation(){
  const json = await fetch(`/api/user-edu/${this.props.user.username}/`).then(response => response.json())
  .then(json =>{this.setState({education: json}); return json;}).catch(err => {
        console.log("fetch error" + err);
        });
      return json;
       }

componentWillMount(){
  this.fetchEducation();
}

  render(){
    var state = this.state;
      return(
      <div>
      <button className="accordion btn-animated"><h2>Education<FaPlus className="expand"/></h2></button>
        <form className="form" >
         <div className="form-group">
          <input className="input-small group-1" onChange={e => this.setState({school: e.target.value})} placeholder="School" type="text"/>
          <input className="input-small group-1" onChange={e => this.setState({degree: e.target.value})} placeholder="Degree" type="text"/>
          <input className="input-small group-1" onChange={e => this.setState({location: e.target.value})} placeholder="Location" type="text"/>
          <input className="input-small group-2" onChange={e => this.setState({start: e.target.value})} id="start" type="date"/>
          <label>to</label>
          <input className="input-small group-2" onChange={e => this.setState({end: e.target.value})} id="end" type="date"/>
         </div>
          <button onClick={this.eduSubmit} className ="submit">Submit</button>
        </form>

        <div>
          {this.state.education.map(el => {
                return <div key={el.id}>
                     <h3 className="main res-item">{el.school}, </h3><span className="res-item">{el.location} -</span>
                     <span className="position">{el.degree} </span>
                     <IconButton className="delete" onClick={()=>this.props.deleteEducation(el.id,state.csrftoken).then(this.fetchEducation())} ><FaTimes/></IconButton>
                     <p className="date res-item">{el.start}-{el.end}</p>
                </div>
            })}
        </div>
    </div>
      );
  }
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
    }
}

const mapDispatchToProps = dispatch => {
  return {
    fetchEducation: () => dispatch(education.fetchEducation()),
    deleteEducation: (id) => dispatch(education.deleteEducation(id)),

  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Education);
