import React, { Component } from 'react';
import {connect} from "react-redux";
import '../static/Home.css';
import {Link} from 'react-router-dom';
import Navbar from './Navbar';
import {FaComments,FaPlus} from 'react-icons/fa';
import IconButton from '@material-ui/core/IconButton';


class Category extends Component{


      state = {
        topics:[],
        category:"",
        hidden:true,
      }

    show = (e) =>{
      this.setState({hidden:!this.state.hidden})
    }

    componentWillMount(){
      fetch(`/api/topic/${this.props.match.params.category_id}/`)
        .then(response => { return response.json();}).then(responseData => {return responseData;})
        .then (json =>this.setState({topics: json}))
        .catch(err => {console.log("fetch error" + err);
         });
    }

    componentDidMount () {
    this.setState({category:this.props.location.state.name})
    }


    render(){
      console.log(this.state.topics)
      return(
      <div id="category">
        <header>
          <Navbar/>
        </header>
        <div className="flex-box">
          <div className="forum-body">
            <div id="recent-topics">
              <h2>Popular Topics</h2>
            </div>

            <div id="topic-list">
              <h1 className="forum-h1">{this.state.category}</h1>
              <table className="table" id="about-me">
                <thead>
                  <tr>
                    <th><IconButton className="new-item"><FaPlus onClick={this.show}/></IconButton> Topic</th>
                    <th>Posts</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td><input className={this.state.hidden ? 'hidden':'input-small'} type="text"/></td>
                    <td></td>
                  </tr>
                {
                  this.state.topics.map((el,idx) => {
                      return[
                        <tr key={el.idx}><td><Link to={"forums/"+el.id}>{el.name}</Link></td><td><FaComments/> {el.posts}</td></tr>
                      ];
                  })
                }
                </tbody>
              </table>

            </div>
          </div>
        </div>
      </div>
      )
    }
  }

const mapStateToProps = state => {
    return {
      user: state.auth.user,
    }
}

export default connect(mapStateToProps)(Category);
