const initialState = {
  education:null,
  isLoading:true,
};


export default function education(state=initialState, action) {
  // let educationList = JSON.parse(state);

  switch (action.type) {
    case 'FETCH_EDUCATION':

      return{...state, education:action.education, isLoading:false};

    case 'ADD_EDUCATION':
      // educationList += action.edu_obj
      return state;

    case 'DELETE_EDUCATION':
      // educationList.splice(action.id,1);
      return state;

    default:
      return state;

  }
}
