import React, { Component } from 'react';
import {connect} from "react-redux";
import {skills} from "../../actions";
import {getCookie} from './get_csrf'
import IconButton from '@material-ui/core/IconButton';
import {FaTimes,FaPlus} from 'react-icons/fa';

class Skills extends Component{

  state ={
    skill:"",
    skills:[],
    csrftoken:getCookie('csrftoken'),
  }

  skillSubmit = e => {
    // e.preventDefault();
    this.addSkill(this.props.user.id,this.state.skill,csrftoken);
  }

addSkill(user_id,skill,csrftoken){
    const token = localStorage.getItem("token");
    let body = JSON.stringify({user_id,interest});
    let headers = {
      'Content-Type': 'application/json',
      'X-CSRFToken': this.state.csrftoken,
    };
    if (token) {
      headers["Authorization"] = `Token ${token}`;
    }

    fetch("/api/skills/", {headers,body,method:"POST",mode:"same-origin"})
    .then(res => {return res.json();}).catch(err => {
              console.log("fetch error" + err)}).then(this.fetchSkills());
  }

deleteSkill(id){
    return(dispatch,getState) => {
      const token = getState().auth.token;
      let headers = {
        'Content-Type': 'application/json',
        'X-CSRFToken': csrftoken,
      };
      let body = JSON.stringify({id});

      if (token) {
        headers["Authorization"] = `Token ${token}`;
      }

      return fetch(`/api/skills/${id}/`, {headers,body,method:"DELETE"})
      .then(res => {
                if (res.status === 204) {
                    return {status: res.status, data: {}};
                } else if (res.status < 500) {
                    return res.json().then(data => {
                        return {status: res.status, data};
                    })
                } else {
                    console.log("Server Error!");
                    throw res;
                }
            })
            .then(res => {
                if (res.status === 204) {
                    return dispatch({type: 'DELETE_SKILL', id});
                } else if (res.status === 401 || res.status === 403) {
                    dispatch({type: "AUTHENTICATION_ERROR", data: res.data});
                    throw res.data;
                }
            })

    }
  }

  fetchSkills(){
    fetch(`/api/user-skills/${this.props.user.username}/`)
     .then(response => { return response.json();}).then(responseData => {return responseData;})
    .then (json =>this.setState({skills: json})).catch(err => {
          console.log("fetch error" + err);
      });
  }

  componentWillMount(){
    this.fetchSkills()
  }

  render(){
      return(
      <div>
      <button className="accordion btn-animated"><h2>Skills<FaPlus className="expand"/></h2></button>
        <form className="form" onSubmit={this.skillSubmit}>
         <div className="form-group">
          <input className="input-small group-1" onChange={e => this.setState({skill: e.target.value})} placeholder="Skill" type="text"/>
          <button className = "submit">Submit</button>
         </div>
        </form>
        <div>
        {this.state.skills.map(el => {
              return <div className="skill" key={el.id}>
                   {el.skill} {el.level}<IconButton className="deleteSkill" onClick={()=>this.props.deleteSkill(el.id,this.state.csrftoken).then(this.props.fetchSkills()).then(window.location.reload(true))} ><FaTimes/></IconButton>

              </div>
          })}
        </div>
      </div>
      );
  }
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
    }
}

const mapDispatchToProps = dispatch => {
  return {
    addSkill:(user_id,skill,csrftoken)=>dispatch(skills.addSkill(user_id,skill,csrftoken)),
    deleteSkill: (id) => dispatch(skills.deleteSkill(id)),

    };
}

export default connect(mapStateToProps, mapDispatchToProps)(Skills);
