import React, { Component } from 'react';
import '../static/Home.css';
import {connect} from 'react-redux';
import {auth} from "../../actions";
import Navbar from './Navbar';
import Popup from '../forms/Popup';

class UserSettings extends Component {

  constructor(props){
    super(props);
    this.state = { showPopup: false };
  }

  togglePopup() {
    this.setState({
     showPopup: !this.state.showPopup
    });
  }

  onSubmit = e => {
    e.preventDefault();
    this.props.login(this.state.username, this.state.password);
  }


  render () {
  return (
    <div id="user-settings">
      <header>
        <Navbar history={this.props.history}/>
      </header>
      <div className="flex-box">
        <div className="connect-body">

        <div className="form">
          <form  onSubmit={this.aboutSave}>
             <div className="form-group">
              <input defaultValue={this.props.user.first_name} className="input-small" onChange={e => this.handleChange("github",e.target.value)}
              maxLength="200" placeholder="Github Url" type="url"/>
              <input defaultValue={this.props.user.last_name} className="input-small " onChange={e => this.handleChange("linkedin",e.target.value)}
              maxLength="100" placeholder="Linkedin Url" type="url"/>
              <input defaultValue={this.props.user.username} className="input-small " onChange={e => this.handleChange("twitter_handle",e.target.value)}
              maxLength="100" placeholder="Twitter Url" type="url"/>
             </div>
             <button className ="submit">Save</button>
          </form>
        </div>

      <h1> Simple Popup Example In React Application </h1>
      <button onClick={this.togglePopup.bind(this)}> Click To Launch Popup</button>

      {this.state.showPopup ?
      <Popup
                text='Click "Close Button" to hide popup'
                closePopup={this.togglePopup.bind(this)}
      />
      : null
      }

        </div>
      </div>
    </div>

      );
   }
}


const mapDispatchToProps = dispatch => {
  return {
    deleteUser: (user) => {
      return dispatch(auth.deleteUser(user));
    }
  };
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
        token: state.auth.token,
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserSettings);
