import React, { Component } from 'react';
import {connect} from "react-redux";
import {interests} from "../../actions";
import {getCookie} from './get_csrf'
import IconButton from '@material-ui/core/IconButton';
import {FaTimes,FaPlus} from 'react-icons/fa';

class Interests extends Component{

  state ={
    interest:"",
    interests:[],
  }

  interestSubmit = e => {
    e.preventDefault();
    var csrftoken = getCookie('csrftoken');
    this.props.addInterest(this.props.user.id,this.state.interest,csrftoken);
    setTimeout(function(){window.location.reload();},10);
  }

  fetchInterests(){

          fetch(`/api/user-interests/${this.props.user.username}/`)
           .then(response => { return response.json();}).then(responseData => {return responseData;})
          .then (json =>this.setState({interests: json})).catch(err => {
                console.log("fetch error" + err);
      });
  }

  componentWillMount(){
    this.fetchInterests()
  }

  render(){
      return(
      <div>
      <button className="accordion btn-animated"><h2>Interests<FaPlus className="expand"/></h2></button>
        <form onSubmit={this.interestSubmit}>
         <div className="form-group">
          <input className="input-small group-1" onChange={e => this.setState({interest: e.target.value})} placeholder="Interest" type="text"/>
          <button className = "submit">Submit</button>
         </div>
        </form>
        <div>
        {this.state.interests.map(el => {
              return <div className="skill" key={el.id}>
                   {el.interest}<IconButton className="deleteSkill" onClick={()=>this.props.deleteInterest(el.id,this.state.csrftoken).then(this.props.fetchInterests()).then(window.location.reload(true))} ><FaTimes/></IconButton>

              </div>
          })}
        </div>
      </div>
      );
  }
}

const mapStateToProps = state => {
    return {
        user: state.auth.user,
    }
}

const mapDispatchToProps = dispatch => {
  return {
    addInterest:(user_id,interest,csrftoken)=>dispatch(interests.addInterest(user_id,interest,csrftoken)),
    deleteInterest: (id) => dispatch(interests.deleteInterest(id))
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(Interests);
