import React, { Component } from 'react';
import {connect} from "react-redux";
import {auth} from "../../actions";
import '../static/Home.css';
import {Link} from 'react-router-dom';


class SearchResult extends Component{

  constructor() {
    super()
    this.state = {
      users:this.props.users,
      hidden:this.props.hidden,
    }
  }

  parse_type(type){
    return (type ==='MR' ? 'Mentor' : 'Mentee')
  }

  render(){
    return(
      this.props.users.map((el,idx) => {
        if(el.id!==this.props.user.id){
          return <div className="user-icon" key={idx}>
          <div>
               <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                 <h3 className="username main res-item">@{el.username}</h3>
               <p className="desc res-item">{this.parse_type(el.type)}</p></Link>
               </div>
                 </div>}
          return null

   }
  )
);
}
}

const mapStateToProps = state => {
    return {
      user: state.auth.user,
    }
}

export default connect(mapStateToProps)(SearchResult);
