import React, { Component } from 'react';
import {header} from 'cirrus-ui';
import {connect} from "react-redux";
import {aboutUser} from "../../actions";
import '../static/AboutForm.css';
import {getCookie} from './get_csrf.js'

class AboutUser extends Component {
  state = {
    location:this.props.aboutUser.location,
    github:this.props.aboutUser.github,
    linkedin:this.props.aboutUser.linkedin,
    twitter_handle:this.props.aboutUser.twitter_handle,
    bio:this.props.aboutUser.bio,
  }

  aboutSubmit = e => {
    e.preventDefault();
    var csrftoken= getCookie('csrftoken');
        this.props.updateAboutUser(this.state.location,this.state.github,this.state.linkedin,this.state.twitter_handle,this.state.bio,csrftoken)
  }


  render () {

      return (
      <div>
        <form onSubmit={this.aboutSubmit}>
         <div className="form-group">
          <input className="input-small group-1" onChange={e => this.setState({github: e.target.value})} placeholder={this.props.aboutUser.github} type="text"/>
          <input className="input-small group-1" onChange={e => this.setState({linkedin: e.target.value})} placeholder={this.props.aboutUser.linkedin} type="text"/>
          <input className="input-small group-1" onChange={e => this.setState({twitter_handle: e.target.value})} placeholder={this.props.aboutUser.twitter_handle} type="text"/>
          <input className="input-small group-2" onChange={e => this.setState({location: e.target.value})} placeholder={this.props.aboutUser.location} type="text"/>
         </div>
         <textarea onChange={e => this.setState({bio: e.target.value})} maxLength="500" placeholder={this.props.aboutUser.bio}></textarea>
         <button className = "submit">Submit</button>
        </form>
      </div>
      );
   }
}

const mapStateToProps = state => {
  let errors = [];
  if (state.auth.errors) {
    errors = Object.keys(state.auth.errors).map(field => {
      return {field, message: state.auth.errors[field]};
    });
  }
  return {
    errors,
    aboutUser: state.aboutUser,
  };
}


const mapDispatchToProps = dispatch => {
  return {
    updateAboutUser: (location,github,linkedin,twitter_handle,bio,csrftoken) => dispatch(aboutUser.updateAboutUser(location,github,linkedin,twitter_handle,bio,csrftoken)),
    };
}


export default connect(mapStateToProps, mapDispatchToProps)(AboutUser);
