import React, { Component } from 'react';
import {connect} from "react-redux";
import {aboutUser} from "../../actions";
import Navbar from './Navbar';
import '../static/About.css';
import Skills from '../forms/Skills'
import Interests from '../forms/Interests'
import Experience from '../forms/Experience'
import Education from '../forms/Education'
import AboutUser from '../forms/AboutUser'

class About extends Component{

  state ={
    avi:"",
    education:[],
    skills:[],
    interests:[],
  }


  componentWillMount(){
    fetch(`/api/avi/${this.props.user.id}/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
     .then (json =>this.setState({avi: json})).catch(err => {
           console.log("fetch error" + err);
       });
          }

  render(){
    return(
    <div className="body">
      <header>
        <Navbar/>
      </header>
      <div className="flex-box">
        <div className="about-body">
          <section id="aboutUser">
             <AboutUser/>
          </section>
          <section id="resume">
            <article>
              <Experience/>
            </article>

            <article>
                <Education/>
            </article>

            <article>
                <Skills/>
            </article>

            <article>
              <Interests/>
            </article>
          </section>
        </div>
      </div>
      <div>{this.props.children}</div>
    </div>
    );
  }
}
const mapStateToProps = state => {
    return {
        user: state.auth.user,
        token: state.auth.token,
    }
}



export default connect(mapStateToProps,mapDispatchToProps)(About);
