import React, { Component } from 'react';
import Navbar from './Navbar';
import '../static/Connect.css';
import {Link} from 'react-router-dom';
import {connect} from "react-redux";
import {FaSearch} from 'react-icons/fa';
import {Tabs,TabLink,TabContent} from 'react-tabs-redux'
import {headers} from '../forms/global.js'


class Connect extends Component{

  state ={
    users:[],
    query:null,
    hidden:false,
    user_query:null,
  }

  componentWillMount(){
    fetch(`/api/users/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
     .then (json =>this.setState({users: json})).catch(err => {
           console.log("fetch error" + err);
       });
  }

  search = (e)=>{
    e.preventDefault();
    this.setState({hidden:true})
    let query = this.state.query
    let body = JSON.stringify({query});
    headers["Authorization"] = `Token ${this.props.token}`;
    fetch("/api/filter/", {headers,body,method:"POST"}).then(res => {return res.json();})
    .then(json =>{this.setState({user_query:json},console.log(json))})
    .catch(err => {console.log("fetch error" + err)})
  }

  reset = (e) =>{
    e.preventDefault();
    this.setState({hidden:false})
    this.setState({user_query:null})
  }

  render(){
    if(this.state.avis===null){
      return<div>Loading</div>
    }
    return(
    <div id="connect">
      <header>
        <Navbar/>
      </header>
    <div className="flex-box">
      <div className="connect-body">
          <div id="search-bar"><input type="text" placeholder="Search..." onChange={e =>this.setState({query:e.target.value})}/>
          <FaSearch onClick={this.search.bind(this)}id="search-icon"/><button id="return" className="submit" onClick={this.reset.bind(this)}>RESET</button></div>
          <Tabs>
            <TabLink className={this.state.hidden ? 'hidden':'tablink'} to="tab1">All</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab2">Location</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab3">Company</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab4">People</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab5">Skill</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab6">Interest</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab7">School</TabLink>


            <TabContent for="tab1">

              <div id="users">
            {
              this.state.users.map((el,idx) => {
                if(el.id!==this.props.user.id && !this.state.hidden){
                  return <div className="user-icon" key={el.id}>
                  <div>
                       <Link to={"/about/"+el.username}><img alt="profile-pic"  className="pro-pic"src={el.avi__avi_path}/>
                         <h3 className="username main res-item">@{el.username}</h3>
                       <p className="desc res-item">{el.type}</p></Link>
                       </div>
                         </div>}
                  return null
              })}
              </div>
            </TabContent>

            <TabContent for="tab2">
                <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.location.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path}  className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
                </div>
            </TabContent>
            <TabContent for="tab3">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.company.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
              </div>
            </TabContent>
            <TabContent for="tab4">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.name.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
              </div>
            </TabContent>
            <TabContent for="tab5">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.skill.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
              </div>
            </TabContent>
            <TabContent for="tab6">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.interest.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
              </div>
            </TabContent>
            <TabContent for="tab7">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.school.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{el.type}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
