import React, { Component } from 'react';
import {connect} from "react-redux";
import '../static/Home.css';
import {Link} from 'react-router-dom';
import Navbar from './Navbar';
import {FaComments} from 'react-icons/fa';


class Category extends Component{


      state = {
        topics:[],
      }

    componentWillMount(){
      fetch(`/api/post/${this.props.match.params.category_id}/`)
        .then(response => { return response.json();}).then(responseData => {return responseData;})
        .then (json =>this.setState({topics: json}))
        .catch(err => {console.log("fetch error" + err);
         });
    }

    componentDidMount () {
     const {category} = this.props.location.state
    }


    render(){
      console.log(this.state.topics)
      return(
      <div id="forum">
        <header>
          <Navbar/>
        </header>
        <div className="flex-box">
          <div className="forum-body">
            <div id="recent-topics">
              <h2>Recent Topics</h2>
            </div>

            <div id="category-list">
              <h1>{category}</h1>
              <table className="table" id="about-me">
                <thead>
                  <tr>
                    <th>Topics</th>
                    <th>Posts</th>
                  </tr>
                </thead>
                <tbody>
                {this.state.topics.map(el => {
                      return[
                        <tr key={el.id}><td><Link to={"forums/"+el.id}>{el.name}</Link></td><td><FaComments/> {el.posts}</td></tr>
                      ];
                  })}
                </tbody>
              </table>

            </div>
          </div>
        </div>
      </div>
      )
    }
  }

const mapStateToProps = state => {
    return {
      user: state.auth.user,
    }
}

export default connect(mapStateToProps)(Category);
