import React, { Component } from 'react';
import Navbar from './Navbar';
import '../static/Connect.css';
import {Link} from 'react-router-dom';
import {connect} from "react-redux";
import {FaSearch} from 'react-icons/fa';
import {Tabs,TabLink,TabContent} from 'react-tabs-redux'
import {headers} from '../forms/global.js'


class Connect extends Component{

  state ={
    users:[],
    query:null,
    hidden:false,
  }

  componentWillMount(){
    fetch(`/api/users/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
      .then (json =>this.setState({users: json}))
      .catch(err => {console.log("fetch error" + err);
       });
  }

  search = (e)=>{
    e.preventDefault();
    this.refs.query.value="";
    let query = this.state.query
    let body = JSON.stringify({query});
    headers["Authorization"] = `Token ${this.props.token}`;
    fetch("/api/filter/", {headers,body,method:"POST"}).then(res => {return res.json();})
      .then(json =>{this.setState({user_query:json})}).then(this.hidden_cb)
      .catch(err => {console.log("fetch error" + err)})


  }

  hidden_cb = (e) =>{
    this.setState({hidden:true});
    console.log(this.state.hidden)
  }

  hidden_false = (e) =>{
    this.setState({hidden:false});
    console.log(this.state.hidden)
  }
  reset = (e) =>{
    e.preventDefault();
    this.setState({query:null},this.hidden_false);
    this.refs.query.value="";
  }

  parse_type(type){
    return (type ==='MR' ? 'Mentor' : 'Mentee')
  }

  render(){
    return(
    <div id="connect">
      <header>
        <Navbar/>
      </header>
    <div className="flex-box">
      <div className="connect-body">
          <div id="search-bar"><input ref="query" type="text" placeholder="Search..." onChange={e =>this.setState({query:e.target.value})}/>
          <FaSearch onClick={this.search.bind(this)}id="search-icon"/><button id="reset" className='submit' onClick={this.reset.bind(this)}>RESET</button></div>
          <p className={!this.state.hidden ? 'hidden':'reset'}>RESULTS FOR: "{this.state.query}"</p>
          <Tabs renderActiveTabContentOnly={true} disableInlineStyles={true}>
            <TabLink className={this.state.hidden ? 'hidden':'tablink'} to="tab1">All</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab2">Location ({this.state.user_query ? this.state.user_query.location.length :null})</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab3">Company ({this.state.user_query ? this.state.user_query.company.length :null})</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab4">People ({this.state.user_query ? this.state.user_query.name.length :null})</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab5">Skill ({this.state.user_query ? this.state.user_query.skill.length :null})</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab6">Interest ({this.state.user_query ? this.state.user_query.interest.length :null})</TabLink>
            <TabLink className={!this.state.hidden ? 'hidden':'tablink'} to="tab7">School ({this.state.user_query ? this.state.user_query.school.length :null})</TabLink>

            <div className={!this.state.hidden ? '':'hidden'}>
            <TabContent for="tab1">

            <div id="users">
            {
              this.state.users.map((el,idx) => {
                if(el.id!==this.props.user.id){
                  return <div className="user-icon" key={idx}>
                  <div>
                       <Link to={"/about/"+el.username}><img alt="profile-pic"  className="pro-pic"src={el.avi__avi_path}/>
                         <h3 className="username main res-item">@{el.username}</h3>
                       <p className="desc res-item">{this.parse_type(el.type)}</p></Link>
                       </div>
                         </div>}
                  return null
              })}
              </div>
            </TabContent>
            </div>
            <TabContent className={!this.state.hidden ? 'hidden':''} for="tab2">
                <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.location.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path}  className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{this.parse_type(el.type)}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
                </div>
            </TabContent>
            <TabContent className={!this.state.hidden ? 'hidden':''} for="tab3">
              <div id="users">
                { this.state.user_query ?(
                  this.state.user_query.company.map((el,idx) => {
                    if(el.id!==this.props.user.id){
                      return <div className="user-icon" key={idx}>
                      <div>
                           <Link to={"/about/"+el.username}><img alt="profile-pic" src={el.avi__avi_path} className="pro-pic"/>
                             <h3 className="username main res-item">@{el.username}</h3>
                           <p className="desc res-item">{this.parse_type(el.type)}</p></Link>
                           </div>
                             </div>}
                      return null
                  })) : null

                }
              </div>
            </TabContent>
            <TabContent className={!this.state.hidden ? 'hidden':''} for="tab4">
