import React, { Component } from 'react';
import {connect} from "react-redux";
import '../static/Home.css';
import Comments from './Comments';
import '../static/Forum.css';
import TextareaAutosize from 'react-textarea-autosize';
import {headers} from '../forms/global.js'
import {Link} from 'react-router-dom';
import {FaComments,FaComment,FaTrash} from 'react-icons/fa';
import IconButton from '@material-ui/core/IconButton';


class Comment extends Component{

  state = {
    meta:{},
    hidden:true,
    reply_hidden:true,
    post:"",
    reply:"",
  }

  show = (e) =>{
    this.setState({hidden:!this.state.hidden})
  }
  showReply = (e) =>{
    this.setState({reply_hidden:!this.state.reply_hidden})
  }


  submitReply =(id) =>{
    this.newPost(this.props.match.params.topic_id,this.props.match.params.category_id,
      this.state.reply,id,this.props.user.id)
  }

  newPost(topic,category,text,reply_to,user_id){
    let body = JSON.stringify({topic,category,text,reply_to,user_id});
    headers["Authorization"] = `Token ${this.props.token}`;
    return fetch(`/api/post/${this.props.match.params.topic_id}/`, {headers,body,method:"POST"}).then(res => {return res.json();})
      .then(json => console.log(json))
      .catch(err => {console.log("fetch error" + err)})
  }


  render(){
     var comment = this.props.comment;
     var meta = this.props.meta;
    return(
      <div>
      <div className="post" >
            <div className="post-avi" style = {meta ? {backgroundImage: `url(${meta[comment.user_id]['avi_path']})`} : {}}></div>
            <div className="text">
              <p> <Link to={"/about/"}>@{meta ? meta[comment.user_id]['username']:""}</Link></p>
              <p>{comment.text}</p>
              <IconButton onClick={this.showReply}><FaComment/></IconButton>
              <IconButton onClick={this.showReply}><FaTrash className={this.props.user.id !== comment.user_id ? "hidden" : ""}/></IconButton>
              <TextareaAutosize  className={this.state.reply_hidden ? "hidden" : ""}id="new-post" onChange={e => this.setState({reply: e.target.value})}
               maxLength="500" minRows={1} maxRows={6}
               placeholder="Reply"/>
              <p onClick={e =>this.submitReply(comment.id)} className={this.state.reply_hidden ? "hidden" : "reply"}>Reply</p>
              <p className="view-replies" onClick={this.show}>{comment.replies ? "View "+comment.replies.length + " replies" : ""}</p>
            </div>
      </div>
      <div className={this.state.hidden ? "hidden" : "replies"}>
        <Comments meta={meta} comments={comment.replies ? comment.replies : []} />
      </div>
      </div>

    )
  }
}


const mapStateToProps = state => {
    return {
        user: state.auth.user,
        token: state.auth.token,
    }
}


export default connect(mapStateToProps)(Comment);
