import React, { Component } from 'react';
import {connect} from "react-redux";
import '../static/Home.css';
import Navbar from './Navbar';
import '../static/Forum.css';
import {FaTimes,FaPlus} from 'react-icons/fa';

class Topic extends Component{

    state = {
      posts:[],
      topic:""
    }

    componentWillMount(){
      fetch(`/api/post/${this.props.match.params.topic_id}/`)
        .then(response => { return response.json();}).then(responseData => {return responseData;})
        .then (json => {this.setState({posts: json.data}); this.setState({topic:json.topic})})
        .catch(err => {console.log("fetch error" + err);
         });
    }

    componentDidMount () {
    this.setState({topic:this.props.location.state.name})
    }



  render(){
    console.log(this.state.posts)
    return(

    <div id="topic">
      <header>
        <Navbar/>
      </header>
      <div className="flex-box">
        <div className="forum-body">
            <div id="post-list">
              <h1 className="forum-h1">{this.state.topic}</h1>
              <textarea onChange={e => this.setState({description: e.target.value})} maxLength="300" placeholder="Description"></textarea>

              <div>
                {this.state.posts.map((el,idx) => {
                      return <div className="edu-ex" key={idx}>
                           <FaTimes className={this.state.hidden ? 'hidden':'deleteSkill'} onClick={(e) => {this.deleteWork(el.id);}} />
                           <p className="desc res-item">{el.text}</p>
                      </div>
                  })}
              </div>
            </div>

        </div>
      </div>
    </div>
    )
  }
}

const mapStateToProps = state => {
    return {
      user: state.auth.user,
    }
}

export default connect(mapStateToProps)(Topic);
