const initialState = {
  interests:null,
  isLoading:true,
};


export default function interests(state=initialState, action) {
  // let interestList = state.slice();

  switch (action.type) {
    case 'FETCH_INTERESTS':
      return{...state,interests:action.interests};

    case 'ADD_INTEREST':
      return state;
      // return[...state, action.interest];

    case 'DELETE_SKILL':
      return state;

      // interestList.splice(action.id,1);
      // return interestList;

    default:
      return state;

  }
}
