import React, { Component } from 'react';
import Navbar from './Navbar';


class Connect extends Component{

  state ={
    users:[],
  }

  componentDidMount(){
    fetch(`/api/users/`)
      .then(response => { return response.json();}).then(responseData => {return responseData;})
     .then (json =>this.setState({users: json})).catch(err => {
           console.log("fetch error" + err);
       });
  }

  getAboutUser(username){
    var aboutUser = {};
    fetch(`/api/aboutUser/${this.props.match.params.username}/`)
      .then(response => { return response.json();}).then(responseData => {return responseData; })
     .then(json=>aboutUser=json).catch(err => {
           console.log("fetch error" + err);
       })
    console.log(aboutUser)
  }

  render(){
    console.log(this.state.users);
    return(
    <div>
      <header>
        <Navbar/>
      </header>

      <div id="body">
        {this.state.users.map(el => {
              return <div key={el.id}>
                   <h3 className="main res-item">{el.username}</h3>
                   <p>{this.getAboutUser(el.username)}</p>

              </div>
          })}


      </div>


    </div>
    );
  }
}

export default (Connect);
